import React, { useState, useEffect, useRef } from 'react';

function App() {
  const [userId, setUserId] = useState('');
  const [toUserId, setToUserId] = useState('');
  const [message, setMessage] = useState('');
  const [receivedMessages, setReceivedMessages] = useState([]);
  const socketRef = useRef(null);

  // Connect to WebSocket
  const connectWebSocket = () => {
    if (!userId) {
      alert('Please enter your user ID');
      return;
    }

    const socket = new WebSocket(`ws://localhost:8080/ws/chat?userId=${userId}`);

    socket.onopen = () => {
      console.log('‚úÖ Connected to WebSocket');
    };

    socket.onmessage = (event) => {
      setReceivedMessages(prev => [...prev, event.data]);
    };

    socket.onclose = () => {
      console.log('‚ùå Disconnected');
    };

    socket.onerror = (error) => {
      console.error('WebSocket Error:', error);
    };

    socketRef.current = socket;
  };

  const sendMessage = () => {
    if (!toUserId || !message) return;
    const formattedMessage = `${toUserId}::${message}`;
    socketRef.current?.send(formattedMessage);
    setMessage('');
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h2>üîê Secure Chat App</h2>

      {!socketRef.current && (
        <>
          <input
            placeholder="Your user ID"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
          />
          <button onClick={connectWebSocket}>Connect</button>
        </>
      )}

      {socketRef.current && (
        <div style={{ marginTop: '1rem' }}>
          <div>
            <input
              placeholder="Send to user ID"
              value={toUserId}
              onChange={(e) => setToUserId(e.target.value)}
            />
          </div>
          <div>
            <input
              placeholder="Your message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>

          <div style={{ marginTop: '1rem' }}>
            <h4>üì• Received Messages:</h4>
            <ul>
              {receivedMessages.map((msg, index) => (
                <li key={index}>üîí {msg}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
